<script lang="ts">import { Root, Trigger, Value, Content, Item, Label } from '../shadcnComponents/ui/select';
let { label, description = undefined, placeholder, isRequired = false, error = undefined, options, ...rest } = $props();
</script>

<Root {...rest}>
  <Label {isRequired}>{label}</Label>
  {#if description}
    <span class="form-item-description">{description}</span>
  {/if}
  <Trigger {error}>
    <Value {placeholder} />
  </Trigger>
  <Content class="border-gray3 border">
    {#each options as option (option.value)}
      <Item value={option.value}>{option.optionLabel}</Item>
    {/each}
  </Content>
  {#if error}
    <span class="form-error">{error}</span>
  {/if}
</Root>
